import React, { Component } from 'react';
import  './clock';
import './weather';
import './advice';
import { firestore } from './firebase';
import './css/style.css';
import $ from 'jquery';
import jQuery from 'jquery';
import { Link, Route, BrowserRouter as Router } from "react-router-dom";
window.$ = window.jQuery = jQuery;

class Main extends Component {

  state = {
    name1 : "", role1 : "", img1 : "",
    name2 : "", role2 : "", img2 : "",
    name3 : "", role3 : "", img3 : ""
  }

  componentDidMount(){
    firestore.collection("user")
    .doc("user1")
    .get()
    .then(doc => {
      this.setState({
        name1 : doc.data().name,
        role1 : doc.data().role,
        img1 : doc.data().img,
        item1 : doc.data().items[0]
      })
    })

    firestore.collection("user")
    .doc("user2")
    .get()
    .then(doc => {
      this.setState({
        name2 : doc.data().name,
        role2 : doc.data().role,
        img2 : doc.data().img
      })
    })

    firestore.collection("user")
    .doc("user3")
    .get()
    .then(doc => {
      this.setState({
        name3 : doc.data().name,
        role3 : doc.data().role,
        img3 : doc.data().img
      })
    })

    function fnMove(seq){
      var offset = $("#" + seq).offset();
      $('#app').animate({scrollTop : offset.top}, 400);
  }

    // const script1 = document.createElement("script");
    // script1.src = "testapp2/src/clock.js";
    // script1.async = true;
    // document.head.appendChild(script1);

    // const script2 = document.createElement("script");
    // script2.src = "testapp2/src/weather.js";
    // script2.async = true;
    // document.head.appendChild(script2);

    // const script3 = document.createElement("script");
    // script3.src = "testapp2/src/advice.js";
    // script3.async = true;
    // document.head.appendChild(script3);
  };

  render(){
    return (
      // <div className = "App">
      //   <h1>ddddd</h1>
      //   <h1> {this.state.name} </h1>
      // </div>

